package a1_출력;

public class HelloJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub 주석
		
		System.out.println("Hello, Java!");
		// System : 운영체제의 기능을 사용
		// out : 운영체제의 기능 중 출력을 담당하는 기능
		// print : 출력을 담당하는 기능 중 입력한만큼만 출력(줄바꿈x)
		// println : 출력을 담당하는 기능 중 한줄을 출력
		// (;) : 세미콜론, 명령의 마지막 지점을 나타냄 
		System.out.println("Hello, Java2!");
		
		// 실행 단축키 (Ctrl + F11)

	}

}